import React from 'react'
import "../styles/App.css"
import ScheduleBlock from './ScheduleBlock'
import svgplus from "../svg/typcn_plus.svg"
import { useState } from 'react'

export default function PlanningPanel() {

  const [numOfSchedules, changeScheduleNums] = useState(2)


  const onChangeScheduleNums = () => {
    changeScheduleNums(numOfSchedules++)
  }

  const ScheduleBlocks = () => {
    var listOfSchedules = Array(numOfSchedules)
    return (listOfSchedules.array.forEach(element => {
      <ScheduleBlock />
    }))
  }
  

  return (
    <div className='content'>
        <div className='panel'>
            <div className='title'>Plan your day</div>
            <div className='schedule'>
            {ScheduleBlocks}
            </div>
            <div className='control'>
              <div className='add-schedule'><img src={svgplus} alt="" /> ADD</div>
              <div className='accept-schedule'>ACCEPT</div>
            </div>

        </div>
    </div>
  )
          }
