import React, { useState } from 'react'
import "../styles/DisplayPlan.css";
import { useEffect } from 'react';




export default function DisplayPlan() {

  const [year, setYear] = useState(12)
  const [month, setMonth] = useState("Aug")
  const [day, setDay] = useState(16)
  const [monthWeekDays, changeMonthWeekDays] = useState([])

  useEffect(()=>{
    const d = new Date()
    setYear(d.getFullYear())
    setMonth(d.toLocaleString('en-US', { month: 'long', lang: "eng" }))
    setDay(d.getDay())
    console.log(new Date(year, month+1, 0))
    // for(var i = 1; i<= new Date(year, month+1, 0).getDate)
  }, [])
  return (

    <div className='DisplayPlan'>
         <div className='panel'>
          <div className='monthTitle'>{month[0].toUpperCase() + month.slice(1,) + " " + year}</div>
          <div className='monthCallendar'>

          </div>
         </div>
    </div>
  )
}
