import React, { useState } from 'react'
import "../styles/DisplayPlan.css";
import { useEffect } from 'react';




export default function DisplayPlan() {

  const d = new Date()
  const [year, setYear] = useState(d.getFullYear())
  const [month, setMonth] = useState(d.toLocaleString('en-US', { month: 'long', lang: "eng" }))
  const [day, setDate] = useState(d.getDate())
  const [monthWeekDays, changeMonthWeekDays] = useState([])
  const [rowsNum, changeRowsNum] = useState(1)
  const [tableContent, changeTableContent] = useState()


  const createTableContent = (rows, weekDays) => {
    const rowArray = [...Array(rows).keys()]
    const colArray = [...Array(7).keys()]
    console.log(weekDays)
    const Content = (
        rowArray.map((index) => {
          return (<tr>
          {colArray.map((index) => {
            if(index === weekDays[0])
            {
              
              weekDays.shift()
              return <td><div className='weekDay'></div>></td>
            }
            else
            {
            return <td>2</td>
            }
          })}</tr>)
        })
    )
    return Content
  }

  useEffect(()=>{
    var numOfRows = 1
    var lastDay = new Date(year, d.getMonth()+1, 0).getDate()
    var weekDays = []
    for(var i = 1; i<= lastDay; i++){
      const w = new Date(year, d.getMonth(), i)
      weekDays.push(w.getDay())
      if(w.getDay()===0)
      {
        numOfRows = numOfRows+1
      } 
      changeRowsNum(numOfRows)   
    }
  

    changeTableContent(createTableContent(numOfRows, weekDays))
    

  }, [])
  

  return (

    <div className='DisplayPlan'>
         <div className='panel'>
          <div className='monthTitle'>{month[0].toUpperCase() + month.slice(1,) + " " + year}</div>
          <div className='monthCallendar'>
          {rowsNum}
          <table>
            <thead>
              <tr>
                <td>Monday</td>
                <td>Tuesday</td>
                <td>Wednesday</td>
                <td>Thursday</td>
                <td>Friday</td>
                <td>Saturday</td>
                <td>Sunday</td>
              </tr>
            </thead>
            <tbody>
              {tableContent}
            </tbody>
          </table>
          </div>
         </div>
    </div>
  )
}
