import React from 'react'
import "../styles/App.css"
import ScheduleBlock from './ScheduleBlock'
import svgplus from "../svg/typcn_plus.svg"
import { useState } from 'react'

export default function PlanningPanel() {

  const [schedules, changeSchedules] = useState([{start:"7", end:"9", name:"breakfast"}, {start:"7", end:"9", name:"breakfast"}])

  
  const onAddSchedule = () => {
    changeSchedules(prevSchedules => [...prevSchedules, {start:"7", end:"9", name:"breakfast"}])
  }

  const ScheduleBlocks = (schedules.map((schedule) => (<ScheduleBlock name = {schedule.name} start = {schedule.start} end = {schedule.end}/>)))

  return (
    <div className='content'>
        <div className='panel'>
            <div className='title'>Plan your day</div>
            <div className='schedule'>
              {ScheduleBlocks}
            </div>
            <div className='control'>
              <div className='add-schedule' onClick={onAddSchedule}><img src={svgplus} alt="" /> ADD</div>
              <div className='accept-schedule'>ACCEPT</div>
            </div>

        </div>
    </div>
  )
          }
