import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState } from 'react';

function App() {
    [width, setWidth] = useState(window.screen.width)
    [height, setHeight] = useState(window.screen.height)

    onChange = () => {
        setWidth(window.screen.width)
        setHeight(window.screen.height)
    }

    return <div onresize = {onChange}>
    <h1>Hello</h1>
    <p>Width : {width}</p>
    <p>Width : {height}</p>
    </div>

}

export default App
